// no 옮길 원반 수, x 시작기둥 y 목표 기둥
static void move(int no, int x, int y) {
	if (no &gt; 1) {
		move(no - 1, x, 6 - x - y);
	}
	if (no &gt; 1) {
		move(no - 1, 6 - x - y, y);
	}
}

